namespace Mirasvit\Helpdesk\Controller\Satisfaction;

class PostTest extends \Magento\TestFramework\TestCase\AbstractController
{
    /**
     * @covers  Mirasvit\Helpdesk\Controller\Satisfaction\Post::execute
     */
    public function testPostAction()
    {
        $this->dispatch('helpdesk/satisfaction/post');
        $body = $this->getResponse()->getBody();
        $this->assertNotEmpty($body);
        $this->assertNotEquals('noroute', $this->getRequest()->getControllerName());
        $this->assertFalse($this->getResponse()->isRedirect());
    }
}
